mapper.select:
select
id, user_id, house_id, created_time, modified_time, deleted, status
from cjhouse.collect


        and id = #{id}


        and user_id = #{userId}


        and house_id = #{houseId}


        and created_time = #{createdTime}


        and modified_time = #{modifiedTime}


        and deleted = #{deleted}


        and status = #{status}
